import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const API = process.env.REACT_APP_API_URL;
function NewPost() {
  const [post, setPost] = useState({});

  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      const postData = await axios.get(API + "/activity/" + id);
      setProduct(postData.data);
    };
    fetchData();
  }, [API]);

  const handleTextChange = (event) => {
    const { id, value } = event.target;
    setPost({ ...post, [id]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    id !== undefined
      ? axios
          .put(API + "/activity/" + id, post)
          .then(() => navigate("/activity/" + id))
      : axios
          .post(API + "/activity", post)
          .then(() => navigate(`/activity`));
  };

  return(
    <div  className="new-post">
      <form className="form-group" onSubmit={handleSubmit}>
        <label htmlFor="category"> Category : </label>
        <input
        className="form-control"
          type="text"
          id="category"
          value={post.category || ""}
          onChange={handleTextChange}
          required
        />

        <label htmlFor="city"> City :</label>
        <input
        className="form-control"
          type="text"
          id="city"
          value={post.city || ""}
          onChange={handleTextChange}
          required
        />

        <label htmlFor="description"> Description :</label>
        <input
        className="form-control"
          type="text"
          id="description"
          value={post.description || ""}
          onChange={handleTextChange}
          required
        />

        <label htmlFor="image"> image : </label>
        <input
        className="form-control"
          type="image"
          id="image"
          value={post.image || ""}
          onChange={handleTextChange}
          required
        />

        <label htmlFor="name"> Name : </label>
        <input
        className="form-control"
          type="text"
          id="name"
          value={post.name || ""}
          onChange={handleTextChange}
          required
        />

        <label htmlFor="state"> State : </label>
        <input
        className="form-control"
          type="text"
          id="state"
          value={post.state || ""}
          onChange={handleTextChange}
          required
        />

           <label htmlFor="street_address"> Street Address : </label>
        <input
        className="form-control"
          type="text"
          id="street_address"
          value={post.street_address || ""}
          onChange={handleTextChange}
          required
        />
           <label htmlFor="zip_code"> Zip Code : </label>
        <input
        className="form-control"
          type="text"
          id="zip_code"
          value={product.zip_code || ""}
          onChange={handleTextChange}
          required
        />

<button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  )
}
export default NewPost;
