import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const API = process.env.REACT_APP_API_URL;
function NewPost() {
  const [post, setPost] = useState({});

  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      const postData = await axios.get(API + "/activity/" + id);
      setPost(postData.data);
    };
    fetchData();
  }, []);

  const handleTextChange = (event) => {
    const { id, value } = event.target;
    setPost({ ...post, [id]: value });
  };
console.log(post)
  const handleSubmit = (event) => {
    event.preventDefault();
    id !== undefined
      ? axios
        .put(API + "/activity/" + id, post)
        .then(() => navigate("/activity/" + id))
      : axios
        .post(API + "/activity/", post)
        .then(() => navigate(`/activity`));
  };

//   <div class="form-outline">
//   <input type="text" id="formControlDefault" class="form-control" />
//   <label class="form-label" for="formControlDefault">Form control default</label>
// </div>

  return (
    <div className="container p-5">
      <form className="form-group" onSubmit={handleSubmit}>
      <div class="form-outline">
        <label class="form-label" htmlFor="name"> Name : </label>
        <input
          className="form-control"
          type="text"
          id="name"
          value={post.name || ""}
          onChange={handleTextChange}
          required
        />
        </div>
        <br />
        <label htmlFor="description"> Description :</label>
        <input
          className="form-control"
          type="text"
          id="description"
          value={post.description || ""}
          onChange={handleTextChange}
          required
        />
        <br />
        <label htmlFor="street_address"> Street Address : </label>
        <input
          className="form-control"
          type="text"
          id="street_address"
          value={post.street_address || ""}
          onChange={handleTextChange}
          required
        />
        <br />
        <label htmlFor="city"> City :</label>
        <input
          className="form-control"
          type="text"
          id="city"
          value={post.city || ""}
          onChange={handleTextChange}
          required
        />
        <br />
        <label htmlFor="state"> State : </label>
        <input
          className="form-control"
          type="text"
          id="state"
          value={post.state || ""}
          onChange={handleTextChange}
          required
        />
        <br />
        <label htmlFor="zip_code"> Zip Code : </label>
        <input
          className="form-control"
          type="text"
          id="zip_code"
          value={post.zip_code || ""}
          onChange={handleTextChange}
          required
        />
        <br />
        <label htmlFor="category"> Category : </label>
        <input
          className="form-control"
          type="text"
          id="category"
          value={post.category || ""}
          onChange={handleTextChange}
          required
        />
        <br />
        <label htmlFor="image"> Image : </label>
        <input
          className="form-control"
          type="url"
          id="image"
          value={post.image || ""}
          onChange={handleTextChange}
          required
        />
        <br />
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  )
}
export default NewPost;
