import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Category from "./Category";
import Content from "./Content";
import SearchActivity from "./SearchActivity";

const API = process.env.REACT_APP_API_URL;
function AllActivities() {
  const [posts, setPosts] = useState([]);
  const [filteredCategory, setFilteredCategory] = useState("All");
  const [search, setSearch] = useState("");
  useEffect(() => {
    axios
      .get(`${API}/activity`)
      .then((response) => {
        setPosts(response.data);
      })
      .catch((error) => console.warn("catch", error));
  }, []);
  console.log(posts);
  // const activities = posts.map((post) => {
  //   return (
  //     <div className="Post">
  //       <Link to={`/Activity/${post.id}`}>
  //         <img
  //           className="post-picture"
  //           src={post.image}
  //           alt={post.name}
  //           width="300"
  //           height="300"
  //         ></img>
  //         <h3>{post.name}</h3>
  //       </Link>
  //     </div>
  //   );
  // });

  const handleCategoryChange = (selectedCat) => {
    setFilteredCategory(selectedCat);
  };

  const selectedCategory =
    filteredCategory === "All"
      ? posts
      : posts.filter((category) => category.category === filteredCategory);

  console.log(selectedCategory);

  return (
    // <section
    // className="AllPosts">{activities}
    // </section>
    <>
      <section>
        <section className="cat-search">
          <div>
            <Category
              handleCategoryChange={handleCategoryChange}
              selected={filteredCategory}
            />
          </div>
          <div>
            <SearchActivity search={search} setSearch={setSearch} />
          </div>
        </section>
        <section className="AllPosts">
          <main>
            <Content
              activity={selectedCategory.filter((activity) =>
                activity.city.includes(search)
              )}
            />
          </main>
        </section>
      </section>
    </>
  );
}

export default AllActivities;
