import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const API = process.env.REACT_APP_API_URL;
function NewPost() {
  const [post, setPost] = useState({});

  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      const postData = await axios.get(API + "/activity/" + id);
      setPost(postData.data);
    };
    fetchData();
  }, []);

  const handleTextChange = (event) => {
    const { id, value } = event.target;
    setPost({ ...post, [id]: value });
  };
console.log(post)
  const handleSubmit = (event) => {
    event.preventDefault();
    id !== undefined
      ? axios
        .put(API + "/activity/" + id, post)
        .then(() => navigate("/activity/" + id))
      : axios
        .post(API + "/activity/", post)
        .then(() => navigate(`/activity`));
  };

//   <div class="form-outline">
//   <input type="text" id="formControlDefault" class="form-control" />
//   <label class="form-label" for="formControlDefault">Form control default</label>
// </div>

  return (<section className="intro">
  <div className="bg-image h-100">
    <div className="mask d-flex align-items-center h-100" 
    style={{backgroundColor: `f3f2f2`}}>
      <div className="container">
        <div className="row d-flex justify-content-center align-items-center">
          <div className="col-12 col-lg-9 col-xl-8">
            <div className="card" style="border-radius: 1rem;">
              <div className="row g-0">
                <div className="col-md-4 d-none d-md-block">
                  <img
                    src="https://mdbootstrap.com/img/Photos/Others/sidenav2.jpg"
                    alt="login form"
                    className="img-fluid" style="border-top-left-radius: 1rem; border-bottom-left-radius: 1rem;"
                  />
                </div>
                <div className="col-md-8 d-flex align-items-center">
                  <div className="card-body py-5 px-4 p-md-5">

                    <form action="">
                      <h4 className="fw-bold mb-4" style="color: #92aad0;">Log in to your account</h4>
                      <p className="mb-4" style="color: #45526e;">To log in, please fill in these text fiels with your e-mail address and password.</p>

                      <div className="form-outline mb-4">
                        <input type="email" id="form2Example1" className="form-control" />
                        <label className="form-label" for="form2Example1">Email address</label>
                      </div>

                      <div className="form-outline mb-4">
                        <input type="password" id="form2Example2" className="form-control" />
                        <label className="form-label" for="form2Example2">Password</label>
                      </div>

                      <div className="d-flex justify-content-end pt-1 mb-4">
                        <button className="btn btn-primary btn-rounded" type="button" style="background-color: #92aad0;">Log in</button>
                      </div>
                      <hr/>
                      <Link className="link float-end" href="#!">Forgot password? Click here.</Link>
                    </form>

                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
  )
}
export default NewPost;
