import axios from "axios";
import React from "react";
import CharacterCounter from 'react-character-counter'
import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./NewPost.css"

const API = process.env.REACT_APP_API_URL;
function NewPost() {
  const [post, setPost] = useState({});

  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      const postData = await axios.get(API + "/activity/" + id);
      setPost(postData.data);
    };
    fetchData();
  }, []);

  const handleTextChange = (event) => {
    const { id, value } = event.target;
    setPost({ ...post, [id]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    id !== undefined
      ? axios
        .put(API + "/activity/" + id, post)
        .then(() => navigate("/activity/" + id))
      : axios
        .post(API + "/activity/", post)
        .then(() => navigate(`/activity`));
  };


  return (
    <div className="container p-2 post">
      <h2>Post your favorite picture</h2>
      <form className="form-group" onSubmit={handleSubmit}>
      <div class="form-outline">
        <label class="form-label" htmlFor="name"> Name : </label>
        <input
          className="form-control form-control-sm"
          type="text"
          id="name"
          value={post.name || ""}
          onChange={handleTextChange}
          required
        />
        </div>

        <div class="form-outline">
        <label class="form-label" htmlFor="description"> Description :</label>
        <textarea
          className="form-control form-control-sm"
          type="text"
          id="description"
          value={post.description || ""}
          onChange={handleTextChange}
          required
        />
        </div>

        <CharacterCounter
  value={this.state.name}
  maxLength={120}
  wrapperStyle={{position: 'relative'}}
  characterCounterStyle={{
    position: "absolute",
    fontSize: "12px",
    fontWeight: "600",
    top: "14px",
    right: "0px"
  }}
>
  <input type='text' maxLength={120}  value={this.state.name}/>
</CharacterCounter>

        <div class="form-outline">
        <label class="form-label" htmlFor="street_address"> Street Address : </label>
        <input
          className="form-control form-control-sm"
          type="text"
          id="street_address"
          value={post.street_address || ""}
          onChange={handleTextChange}
          required
        />
        </div>

        <div class="form-outline">

        <label class="form-label" htmlFor="city"> City :</label>
        <input
          className="form-control form-control-sm"
          type="text"
          id="city"
          value={post.city || ""}
          onChange={handleTextChange}
          required
        />
        </div>

        <div class="form-outline">
        <label class="form-label" htmlFor="state"> State : </label>
        <input
          className="form-control form-control-sm"
          type="text"
          id="state"
          value={post.state || ""}
          onChange={handleTextChange}
          required
        />
        </div>

        <div class="form-outline">
        <label class="form-label" htmlFor="zip_code"> Zip Code : </label>
        <input
          className="form-control form-control-sm"
          type="text"
          id="zip_code"
          value={post.zip_code || ""}
          onChange={handleTextChange}
          required
        />
        </div>

        <div class="form-outline">
        <label class="form-label" htmlFor="category"> Category : </label>
        <input
          className="form-control form-control-sm"
          type="text"
          id="category"
          value={post.category || ""}
          onChange={handleTextChange}
          required
        />
        </div>

        <div class="form-outline">
        <label class="form-label" htmlFor="image"> Image : </label>
        <input
          className="form-control form-control-sm"
          type="url"
          id="image"
          value={post.image || ""}
          onChange={handleTextChange}
          required
        /></div>
       <br/>
        <button type="submit" className="btn btn-secondary">Submit</button>
      </form>
    </div>
  )
}
export default NewPost;
